});
});

section.script:
document.querySelectorAll(".toggle").forEach(p => {
p.addEventListener("click", function () {
let dropdown = this.nextElementSibling;

if (dropdown.classList.contains("open")) {
dropdown.classList.remove("open");
this.classList.remove("open-arrow");
} else {
document.querySelectorAll(".dropdown-container").forEach(ul => ul.classList.remove("open"));
document.querySelectorAll(".toggle").forEach(toggle =>
toggle.classList.remove("open-arrow"));
dropdown.classList.add("open");
this.classList.add("open-arrow");
}
});
});

document.addEventListener("DOMContentLoaded", function () {
const dropdowns = document.querySelectorAll(".custom-dropdown");

dropdowns.forEach((dropdown) => {
console.log('gggggg');
const toggle = dropdown.querySelector(".custom-dropdown-toggle");
const menu = dropdown.querySelector(".custom-dropdown-menu");

if (toggle) {
toggle.addEventListener("click", function (event) {
event.stopPropagation();

const parentDropdown = dropdown.closest('.custom-dropdown');
const siblingDropdowns = parentDropdown
? parentDropdown.parentElement.querySelectorAll(':scope > .custom-dropdown')
: dropdowns;

siblingDropdowns.forEach((otherDropdown) => {
if (otherDropdown !== dropdown) {
console.log('tttttttt');
otherDropdown.classList.remove("active");
const otherMenu = otherDropdown.querySelector(".custom-dropdown-menu");
if (otherMenu) otherMenu.classList.remove("active");
}
});

if (menu) {
menu.classList.toggle("active");
dropdown.classList.toggle("active");
}

const divId = toggle.getAttribute('data-div-id');
if (divId === 'whole') {
console.log('gggggg');
const parentCategoryMenu = document.querySelectorAll('.category-accordion
.custom-dropdown');
const parentCategoryMenuItem = document.querySelectorAll('.category-accordion
.custom-dropdown-menu');
console.log('parentCategoryMenu', parentCategoryMenu, 'parentCategoryMenuItem',
parentCategoryMenuItem);

const staticContentDivs = document.querySelectorAll('.center-div');
const displayDiv = document.querySelector('#category-display');
const subcategoryGrid = document.querySelector('#subcategory-grid');
const staticGrid = document.querySelector('#static-grid');
const subcategoryGroups = document.querySelectorAll('.subcategory-group');
const imageGridDisplay = document.querySelector('.image_grid_display');

staticContentDivs.forEach((div) => {
if (div.id !== divId) {
div.classList.add('d-none');
}
});
if (displayDiv) displayDiv.classList.add('d-none');
if (subcategoryGrid) subcategoryGrid.classList.add('d-none');
if (staticGrid) staticGrid.classList.add('d-none');
subcategoryGroups.forEach((group) => {
group.classList.add('d-none');
});

const targetDiv = document.querySelector(`#${divId}`);
if (targetDiv) {
targetDiv.classList.remove('d-none');
console.log(`Showing div: #${divId}`);
parentCategoryMenu.forEach((menu) => {
menu.classList.remove('active');
menu.classList.remove('show');
const closestDropdown = menu.closest('.custom-dropdown');
if (closestDropdown) closestDropdown.classList.remove('active');
});
parentCategoryMenuItem.forEach((menuItem) => {
menuItem.classList.remove('active');
menuItem.classList.remove('show');
const closestMenu = menuItem.closest('.custom-dropdown-menu');
if (closestMenu) closestMenu.classList.remove('active');
});
} else {
console.warn(`No div found for ID: ${divId}`);
}

if (divId === 'whole' && staticGrid) {
staticGrid.classList.remove('d-none');
}

if (imageGridDisplay) {
imageGridDisplay.innerHTML = '';
const logoImg = document.createElement('img');
logoImg.className = 'w-100 main_logo';
logoImg.style.maxWidth = '300px';
logoImg.src = '/FCS_PAY_OLD/static/src/images/new_fcslogo.png';
logoImg.alt = 'Default Logo';
logoImg.onerror = function () {
console.error('Failed to load default logo at
/FCS_PAY_OLD/static/src/images/new_fcslogo.png');
};
imageGridDisplay.appendChild(logoImg);
console.log('Rendered default FCS logo in .image_grid_display');
} else {
console.warn('Could not find .image_grid_display to render main_logo');
}
}
});
}
});

document.addEventListener("click", function (event) {
if (!event.target.closest('.custom-dropdown')) {
dropdowns.forEach((dropdown) => {
dropdown.classList.remove("active");
const menu = dropdown.querySelector(".custom-dropdown-menu");
if (menu) menu.classList.remove("active");
});
}
});
});

