section.script:
document.addEventListener('DOMContentLoaded', () => {
    const dropdown = document.querySelector('.o_custom_dropdown_snippet .custom-dropdown');
    const subbuttonsWrapper = document.querySelector('.o_custom_dropdown_snippet .subbuttons-wrapper');
    if (dropdown && subbuttonsWrapper) {
        const updateSubbuttons = () => {
            const selectedValue = dropdown.value;
            subbuttonsWrapper.querySelectorAll('button').forEach(button => {
                button.style.display = button.dataset.option === selectedValue ? '' : 'none';
            });
        };
        dropdown.addEventListener('change', updateSubbuttons);
        updateSubbuttons(); // Initial call to set correct subbuttons
    }
});
